import React from 'react';
import { useFormContext, Controller } from 'react-hook-form';
import styles from './Step3.module.css';

const Step3 = () => {
  const { control } = useFormContext();

  return (
    <div className={styles.container}>
      <h2 className={styles.title}>Etapa 3: Confirmação</h2>
      <div className={styles.inputGroup}>
        <label className={styles.label}>
          <Controller
            name="termos"
            control={control}
            defaultValue={false}
            rules={{ required: 'Você deve concordar com os termos' }}
            render={({ field }) => (
              <input
                type="checkbox"
                {...field}
                className={styles.checkbox}
              />
            )}
          />
          <span className={styles.checkboxLabel}>Concordo com os termos e condições</span>
        </label>
      </div>
    </div>
  );
};

export default Step3;
