import React from 'react';
import { useFormContext, Controller } from 'react-hook-form';
import styles from './Step2.module.css';

const Step2 = () => {
  const { control } = useFormContext();

  return (
    <div className={styles.container}>
      <h2 className={styles.title}>Empresa</h2>
      <div className={styles.inputGroup}>
        <label className={styles.label}>Rua:</label>
        <Controller
          name="nome da empresa"
          control={control}
          defaultValue=""
          rules={{ required: 'Rua é obrigatória' }}
          render={({ field }) => (
            <input {...field} className={styles.input} placeholder="Digite sua rua" />
          )}
        />
      </div>
      <div className={styles.inputGroup}>
        <label className={styles.label}>Cidade:</label>
        <Controller
          name="numero de funcionarios"
          control={control}
          defaultValue=""
          rules={{ required: 'Cidade é obrigatória' }}
          render={({ field }) => (
            <input {...field} className={styles.input} placeholder="Digite sua cidade" />
          )}
        />
      </div>
      <div className={styles.inputGroup}>
        <label className={styles.label}>Cidade:</label>
        <Controller
          name="sobre o seu negocio"
          control={control}
          defaultValue=""
          rules={{ required: 'Cidade é obrigatória' }}
          render={({ field }) => (
            <textarea {...field} className={styles.input} placeholder="Digite sua cidade" />
          )}
        />
      </div>
    </div>
  );
};

export default Step2;
